import * as types from "../../actionTypes";
import ItemApi from "../../../api/ItemApi";

let actionsFunction = {

  //CRUD METHODS

  // Create item
  createItem: function(item) {
    return function(dispatch) {
      return ItemApi
        .createItem(item)
        .then(item => {
          dispatch(actionsFunction.createItemSuccess(item));
        })
        .catch(error => {
          throw error;
        });
    };
  },

  createItemSuccess: function(item) {
    return { type: types.CREATE_ITEM_SUCCESS, payload: item };
  },


  // Get item
  loadItem: function(id) {
    return function(dispatch) {
      return ItemApi
        .getOneItem(id)
        .then(item => {
          dispatch(actionsFunction.loadItemSuccess(item));
        })
        .catch(error => {
          throw error;
        });
    };
  },

  loadItemSuccess: function(item) {
    return { type: types.GET_ITEM_SUCCESS, payload: item };
  },

	
  // Save item
  saveItem: function(item) {
    return function(dispatch) {
      return ItemApi
        .saveItem(item)
        .then(item => {
          dispatch(actionsFunction.saveItemSuccess(item));
        })
        .catch(error => {
          throw error;
        });
    };
  },

  saveItemSuccess: function(item) {
    return { type: types.UPDATE_ITEM_SUCCESS, payload: item };
  },


};

export default actionsFunction;
